package operators;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=6;
		int y=2;
		int z=10;
		int sum=x+y+z;
		double div=x/y;
		int prod=x*y*z;
		int rem=z%x;
		System.out.println(sum);
		System.out.println(div);
		System.out.println(prod);
		System.out.println(rem);
	}

}
